mapper.select:
SELECT user_id
FROM user
WHERE user_id = #{userId}

select user_id AS userId, password, name, email, gender, age_group AS ageGroup, addr_group AS addrGroup
from user
where user_id = #{userId}

	select user_id AS userId, password, name, email, gender, age_group AS ageGroup, addr_group AS addrGroup
    from user
	where user_id = #{userId} AND password = #{password}

mapper.update:
UPDATE user
SET refresh_token = #{refreshToken}
WHERE user_id = #{userId}

UPDATE user
SET refresh_token = ''
WHERE user_id = #{userId}
